from freedomrobotics.link_protobuf_base import LinkProtobufBase


class LinkProtobuf(LinkProtobufBase):

    def __init__(self, name="link_protobuf", protobuf_command_callback=None, credentials_location=None):
        """
        Initializes a Link object which has both standard message methods
        and protobuf ones.
        """
        self.protobuf_command_callback = protobuf_command_callback
        super(LinkProtobuf, self).__init__(
            name, protobuf_command_callback=self.protobuf_command_callback, credentials_location=credentials_location)
        self._cached_command_types = {}
